}

 String CurrentTime(){
    var date = DateTime.now().toString();
    var dateParse = DateTime.parse(date);
    var formattedDate = "${dateParse.day}/${dateParse.month}/${dateParse.year} ${dateParse.hour}:${dateParse.minute}";
    String currentTime = formattedDate.toString();
    return currentTime;
  }

String GetBrisbaneTime(String pstTime){
  var dateParse = DateTime.parse(pstTime);
  dateParse=dateParse.add(Duration(hours: userTimeZoneOffset));
  var formattedDate = "${dateParse.day}/${dateParse.month}/${dateParse.year} ${dateParse.hour}:${dateParse.minute}";
  String currentTime = formattedDate.toString();
  return currentTime;
}